st-child {\n  border-right: none;\n}\n.contatos-table[_ngcontent-%COMP%]   tbody[_ngcontent-%COMP%]   .loading-cell[_ngcontent-%COMP%], .contatos-table[_ngcontent-%COMP%]   tbody[_ngcontent-%COMP%]   .empty-cell[_ngcontent-%COMP%] {\n  text-align: center;\n  padding: 2rem;\n  color: #6c757d;\n  font-style: italic;\n}\n.contatos-table[_ngcontent-%COMP%]   tbody[_ngcontent-%COMP%]   .nome-cell[_ngcontent-%COMP%] {\n  min-width: 150px;\n}\n@media (max-width: 768px) {\n  .contatos-table[_ngcontent-%COMP%]   tbody[_ngcontent-%COMP%]   .nome-cell[_ngcontent-%COMP%] {\n    min-width: 100px;\n  }\n}\n@media (max-width: 480px) {\n  .contatos-table[_ngcontent-%COMP%]   tbody[_ngcontent-%COMP%]   .email-cell[_ngcontent-%COMP%] {\n    display: none;\n  }\n}\n.contatos-table[_ngcontent-%COMP%]   tbody[_ngcontent-%COMP%]   .data-cell[_ngcontent-%COMP%] {\n  min-width: 100px;\n}\n@media (max-width: 480px) {\n  .contatos-table[_ngcontent-%COMP%]   tbody[_ngcontent-%COMP%]   .data-cell[_ngcontent-%COMP%] {\n    display: none;\n  }\n}\n.contatos-table[_ngcontent-%COMP%]   .actions[_ngcontent-%COMP%] {\n  display: flex;\n  gap: 0.5rem;\n  justify-content: center;\n  min-width: 80px;\n}\n@media (max-width: 480px) {\n  .contatos-table[_ngcontent-%COMP%]   .actions[_ngcontent-%COMP%] {\n    flex-direction: column;\n    gap: 0.2rem;\n  }\n}\n.contatos-table[_ngcontent-%COMP%]   .btn-edit[_ngcontent-%COMP%], .contatos-table[_ngcontent-%COMP%]   .btn-delete[_ngcontent-%COMP%] {\n  background: none;\n  border: none;\n  cursor: pointer;\n  padding: 0.4rem;\n  border-radius: 4px;\n  transition: background-color 0.3s ease;\n}\n.contatos-table[_ngcontent-%COMP%]   .btn-edit[_ngcontent-%COMP%]:hover:not(:disabled), .contatos-table[_ngcontent-%COMP%]   .btn-delete[_ngcontent-%COMP%]:hover:not(:disabled) {\n  background-color: #e9ecef;\n}\n.contatos-table[_ngcontent-%COMP%]   .btn-edit[_ngcontent-%COMP%]:disabled, .contatos-table[_ngcontent-%COMP%]   .btn-delete[_ngcontent-%COMP%]:disabled {\n  opa