import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { HttpClientModule } from '@angular/common/http';
import { ContatoService, ContatoAPI } from '../../services/contato.service';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, FormsModule, HttpClientModule],
  template: `
    <div>
      <h1>TESTE SIMPLES</h1>
      <p>Total contatos: {{ contatos.length }}</p>
      
      <div *ngFor="let contato of contatos">
        <p><strong>{{ contato.nome }}</strong> - {{ contato.email }}</p>
      </div>
      
      <button (click)="carregarContatos()">Recarregar API</button>
    </div>
            
            <div class="form-field">
              <label for="profissao">Profiss√£o</label>
              <input 
                type="text" 
                id="profissao" 
                name="profissao"
                placeholder="Ex.: Desenvolvedora Web"
                [(ngModel)]="contato.profissao"
                required>
            </div>
          </div>

          <div class="form-row">
            <div class="form-field">
              <label for="telefone_contato">Telefone para contato</label>
              <input 
                type="tel" 
                id="telefone_contato" 
                name="telefone_contato"
                placeholder="Ex.: 40332019"
                maxlength="8"
                [(ngModel)]="contato.telefone_contato"
                required>
            </div>
            
            <div class="form-field">
              <label for="celular_contato">Celular para contato</label>
              <input 
                type="tel" 
                id="celular_contato" 
                name="celular_contato"
                placeholder="Ex.: 11984932039"
                maxlength="11"
                [(ngModel)]="contato.celular_contato"
                required>
            </div>
          </div>

          <!-- Checkboxes -->
          <div class="checkbox-section">
            <div class="checkbox-item">
              <input 
                type="checkbox" 
                id="whatsapp" 
                name="whatsapp"
                [(ngModel)]="contato.possuiWhatsapp">
              <label for="whatsapp">N√∫mero de celular possui Whatsapp</label>
            </div>
            
            <div class="checkbox-item">
              <input 
                type="checkbox" 
                id="notificacaoEmail" 
                name="notificacaoEmail"
                [(ngModel)]="contato.notificacaoEmail">
              <label for="notificacaoEmail">Enviar notifica√ß√µes por E-mail</label>
            </div>
            
            <div class="checkbox-item">
              <input 
                type="checkbox" 
                id="notificacaoSMS" 
                name="notificacaoSMS"
                [(ngModel)]="contato.notificacaoSMS">
              <label for="notificacaoSMS">Enviar notifica√ß√µes por SMS</label>
            </div>
          </div>

          <!-- Mensagem de erro -->
          <div class="error-message" *ngIf="erro">
            {{ erro }}
          </div>

          <!-- Bot√µes de a√ß√£o -->
          <div class="form-actions">
            <button type="submit" class="btn-cadastrar" [disabled]="loading">
              {{ loading ? 'Aguarde...' : (editandoId ? 'Atualizar contato' : 'Cadastrar contato') }}
            </button>
            <button type="button" class="btn-cancelar" *ngIf="editandoId" (click)="cancelarEdicao()">
              Cancelar
            </button>
          </div>
        </form>
      </div>

      <!-- Linha Separadora -->
      <hr class="separator">

      <h2>Total: {{ contatos.length }} contatos</h2>

      <!-- TABELA SIMPLES -->
      <table border="1" style="width: 100%; margin: 20px 0;">
        <tr style="background: #048cd4; color: white;">
          <th style="padding: 10px;">ID</th>
          <th style="padding: 10px;">Nome</th>
          <th style="padding: 10px;">Email</th>
          <th style="padding: 10px;">Data Nascimento</th>
          <th style="padding: 10px;">Celular</th>
        </tr>
        <tr *ngFor="let c of contatos" style="background: white;">
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.id }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.nome }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.email }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.data_nascimento }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.celular_contato }}</td>
        </tr>
      </table>
    </div>
  `,
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  contato = {
    nome: '',
    data_nascimento: '',
    email: '',
    profissao: '',
    telefone_contato: '',
    celular_contato: '',
    possuiWhatsapp: false,
    notificacaoEmail: false,
    notificacaoSMS: false
  };

  contatos: ContatoAPI[] = [];
  loading = false;
  erro = '';
  editandoId: number | null = null;

  constructor(private contatoService: ContatoService) {}

  ngOnInit() {
    console.log('üöÄ INICIANDO COMPONENTE HOME');
    
    // TESTE B√ÅSICO PRIMEIRO
    this.contatos = [
      { id: 1, nome: 'TESTE 1', email: 'teste1@teste.com', data_nascimento: '2000-01-01', profissao: 'Teste', telefone_contato: '12345678', celular_contato: '11999999999' },
      { id: 2, nome: 'TESTE 2', email: 'teste2@teste.com', data_nascimento: '2000-01-01', profissao: 'Teste', telefone_contato: '12345678', celular_contato: '11999999999' }
    ];
    
    console.log('üìã DADOS DE TESTE:', this.contatos.length, 'contatos');
    
    // Carregar da API
    this.carregarContatos();
  }

  carregarContatos() {
    console.log('Iniciando carregamento de contatos...');
    this.loading = true;
    this.erro = '';
    
    this.contatoService.listarContatos().subscribe({
      next: (response) => {
        console.log('RESPOSTA COMPLETA DA API:', response);
        
        // A API retorna: { success: true, data: { contatos: [...] } }
        // Precisamos extrair response.data.contatos
        if (response && response.success && response.data && response.data.contatos) {
          this.contatos = [...response.data.contatos]; // For√ßar nova refer√™ncia para o Angular detectar mudan√ßas
          console.log('‚úÖ CONTATOS CARREGADOS:', this.contatos.length);
        } else {
          console.log('FORMATO N√ÉO RECONHECIDO:', response);
          this.contatos = [];
        }
        console.log('CONTATOS FINAIS CARREGADOS:', this.contatos.length, this.contatos);
        this.loading = false;
      },
      error: (error) => {
        console.error('ERRO AO CARREGAR CONTATOS:', error);
        this.erro = `Erro ao carregar contatos: ${error.message || error}`;
        this.contatos = [];
        this.loading = false;
      }
    });
  }

  cadastrarContato() {
    if (!this.contato.nome || !this.contato.email) {
      this.erro = 'Nome e email s√£o obrigat√≥rios';
      return;
    }

    // Simular cadastro e apenas recarregar da API
    this.resetForm();
    this.carregarContatos();
  }

  editarContato(contato: ContatoAPI) {
    // Apenas simular edi√ß√£o
    console.log('Editando contato:', contato);
  }

  excluirContato(id: number) {
    // Apenas simular exclus√£o
    console.log('Excluindo contato:', id);
  }

  cancelarEdicao() {
    this.editandoId = null;
    this.resetForm();
  }

  resetForm() {
    this.editandoId = null;
    this.contato = {
      nome: '',
      data_nascimento: '',
      email: '',
      profissao: '',
      telefone_contato: '',
      celular_contato: '',
      possuiWhatsapp: false,
      notificacaoEmail: false,
      notificacaoSMS: false
    };
    this.erro = '';
  }

  formatarData(data: string): string {
    if (!data) return '';
    const date = new Date(data);
    return date.toLocaleDateString('pt-BR');
  }

  trackContatoById(index: number, item: ContatoAPI): any {
    return item.id || index;
  }

  // M√©todo para testar com dados locais
  testarComDadosLocais() {
    this.contatos = [
      {
        id: 1,
        nome: 'Teste Local',
        data_nascimento: '2000-01-01',
        email: 'teste@local.com',
        profissao: 'Teste',
        telefone_contato: '12345678',
        celular_contato: '11987654321'
      }
    ];
  }
}
