import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { HttpClientModule } from '@angular/common/http';
import { ContatoService, ContatoAPI } from '../../services/contato.service';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, FormsModule, HttpClientModule],
  template: `
    <div class="container">
      <!-- Formulário de Cadastro -->
      <div class="form-container">
        <form class="contact-form" (ngSubmit)="cadastrarContato()">
          <div class="form-row">
            <div class="form-field">
              <label for="nome">Nome completo</label>
              <input 
                type="text" 
                id="nome" 
                name="nome"
                placeholder="Ex.: Letícia Pacheco dos Santos"
                [(ngModel)]="contato.nome"
                required>
            </div>
            
            <div class="form-field">
              <label for="data_nascimento">Data de nascimento</label>
              <input 
                type="date" 
                id="data_nascimento" 
                name="data_nascimento"
                [(ngModel)]="contato.data_nascimento"
                required>
            </div>
          </div>

          <div class="form-row">
            <div class="form-field">
              <label for="email">E-mail</label>
              <input 
                type="email" 
                id="email" 
                name="email"
                placeholder="Ex.: leticia@gmail.com"
                [(ngModel)]="contato.email"
                required>
            </div>
            
            <div class="form-field">
              <label for="profissao">Profissão</label>
              <input 
                type="text" 
                id="profissao" 
                name="profissao"
                placeholder="Ex.: Desenvolvedora Web"
                [(ngModel)]="contato.profissao"
                required>
            </div>
          </div>

          <div class="form-row">
            <div class="form-field">
              <label for="telefone_contato">Telefone para contato</label>
              <input 
                type="tel" 
                id="telefone_contato" 
                name="telefone_contato"
                placeholder="Ex.: 40332019"
                maxlength="8"
                [(ngModel)]="contato.telefone_contato"
                required>
            </div>
            
            <div class="form-field">
              <label for="celular_contato">Celular para contato</label>
              <input 
                type="tel" 
                id="celular_contato" 
                name="celular_contato"
                placeholder="Ex.: 11984932039"
                maxlength="11"
                [(ngModel)]="contato.celular_contato"
                required>
            </div>
          </div>

          <!-- Mensagem de erro -->
          <div class="error-message" *ngIf="erro">
            {{ erro }}
          </div>

          <!-- Botões de ação -->
          <div class="form-actions">
            <button type="submit" class="btn-cadastrar" [disabled]="loading">
              {{ loading ? 'Aguarde...' : (editandoId ? 'Atualizar contato' : 'Cadastrar contato') }}
            </button>
            <button type="button" class="btn-cancelar" *ngIf="editandoId" (click)="cancelarEdicao()">
              Cancelar
            </button>
          </div>
        </form>
      </div>

      <!-- Linha Separadora -->
      <hr class="separator">

      <!-- Tabela de Contatos -->
      <div class="table-container" *ngIf="contatos.length > 0">
        <table class="contatos-table">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Data de nascimento</th>
              <th>E-mail</th>
              <th>Celular para contato</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngIf="loading">
              <td colspan="5" class="loading-cell">Carregando contatos...</td>
            </tr>
            <tr *ngIf="!loading && contatos.length === 0">
              <td colspan="5" class="empty-cell">Nenhum contato encontrado</td>
            </tr>
            <tr *ngFor="let contato of contatos" [class.editing]="editandoId === contato.id">
              <td class="nome-cell">{{ contato.nome }}</td>
              <td class="data-cell">{{ formatarData(contato.data_nascimento) }}</td>
              <td class="email-cell">{{ contato.email }}</td>
              <td class="celular-cell">{{ contato.celular_contato }}</td>
              <td class="actions">
                <button class="btn-edit" (click)="editarContato(contato)" [disabled]="loading">
                  <img src="assets/editar.png" alt="Editar" class="action-icon">
                </button>
                <button class="btn-delete" (click)="excluirContato(contato.id!)" [disabled]="loading">
                  <img src="assets/excluir.png" alt="Excluir" class="action-icon">
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  `,
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  contato = {
    nome: '',
    data_nascimento: '',
    email: '',
    profissao: '',
    telefone_contato: '',
    celular_contato: ''
  };

  contatos: ContatoAPI[] = [];
  loading = false;
  erro = '';
  editandoId: number | null = null;

  constructor(private contatoService: ContatoService) {}

  ngOnInit() {
    this.carregarContatos();
  }

  carregarContatos() {
    this.loading = true;
    this.contatoService.listarContatos().subscribe({
      next: (response) => {
        if (response.success && Array.isArray(response.data)) {
          this.contatos = response.data;
        } else {
          this.contatos = response.data ? [response.data] : [];
        }
        this.loading = false;
      },
      error: (error) => {
        console.error('Erro ao carregar contatos:', error);
        this.erro = 'Erro ao carregar contatos';
        this.loading = false;
        this.contatos = []; // Manter array vazio em caso de erro
      }
    });
  }

  cadastrarContato() {
    if (!this.contato.nome || !this.contato.email) {
      this.erro = 'Nome e email são obrigatórios';
      return;
    }

    this.loading = true;
    this.erro = '';

    if (this.editandoId) {
      // Atualizar contato existente
      this.contatoService.atualizarContato(this.editandoId, this.contato).subscribe({
        next: (response) => {
          if (response.success) {
            this.carregarContatos();
            this.resetForm();
          }
          this.loading = false;
        },
        error: (error) => {
          console.error('Erro ao atualizar contato:', error);
          this.erro = 'Erro ao atualizar contato';
          this.loading = false;
        }
      });
    } else {
      // Criar novo contato
      this.contatoService.criarContato(this.contato).subscribe({
        next: (response) => {
          if (response.success) {
            this.carregarContatos();
            this.resetForm();
          }
          this.loading = false;
        },
        error: (error) => {
          console.error('Erro ao cadastrar contato:', error);
          this.erro = 'Erro ao cadastrar contato';
          this.loading = false;
        }
      });
    }
  }

  editarContato(contato: ContatoAPI) {
    this.editandoId = contato.id!;
    this.contato = {
      nome: contato.nome,
      data_nascimento: contato.data_nascimento,
      email: contato.email,
      profissao: contato.profissao,
      telefone_contato: contato.telefone_contato,
      celular_contato: contato.celular_contato
    };
  }

  excluirContato(id: number) {
    if (confirm('Tem certeza que deseja excluir este contato?')) {
      this.loading = true;
      this.contatoService.excluirContato(id).subscribe({
        next: (response) => {
          if (response.success) {
            this.carregarContatos();
          }
          this.loading = false;
        },
        error: (error) => {
          console.error('Erro ao excluir contato:', error);
          this.erro = 'Erro ao excluir contato';
          this.loading = false;
        }
      });
    }
  }

  cancelarEdicao() {
    this.editandoId = null;
    this.resetForm();
  }

  resetForm() {
    this.editandoId = null;
    this.contato = {
      nome: '',
      data_nascimento: '',
      email: '',
      profissao: '',
      telefone_contato: '',
      celular_contato: ''
    };
    this.erro = '';
  }

  formatarData(data: string): string {
    if (!data) return '';
    const date = new Date(data);
    return date.toLocaleDateString('pt-BR');
  }
}
