sourcesContent:
import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ContatoService, ContatoAPI } from '../../services/contato.service';

export interface Contato {
  nome: string;
  data_nascimento: string;
  email: string;
  profissao: string;
  telefone_contato: string;
  celular_contato: string;
  possuiWhatsapp: boolean;
  notificacaoEmail: boolean;
  notificacaoSMS: boolean;
}

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="container">
      <!-- Header -->
      <div class="form-header">
        <h1>Cadastro de contato</h1>
      </div>

      <!-- FormulÃ¡rio -->
      <div class="form-container">
        <form (ngSubmit)="salvarContato()">
          <!-- Primeira linha -->
          <div class="form-row">
            <div class="form-field">
              <label for="nome">Nome</label>
              <input 
                type="text" 
                id="nome" 
                name="nome"
                placeholder="Ex.: JoÃ£o da Silva"
                [(ngModel)]="contato.nome"
                required>
            </div>
            <div class="form-field">
              <label for="data_nascimento">Data de nascimento</label>
              <input 
                type="date" 
                id="data_nascimento" 
                name="data_nascimento"
                [(ngModel)]="contato.data_nascimento"
                required>
            </div>
          </div>

          <!-- Segunda linha -->
          <div class="form-row">
            <div class="form-field">
              <label for="email">E-mail</label>
              <input 
                type="email" 
                id="email" 
                name="email"
                placeholder="Ex.: joao@teste.com.br"
                [(ngModel)]="contato.email"
                required>
            </div>
            <div class="form-field">
              <label for="profissao">ProfissÃ£o</label>
              <input 
                type="text" 
                id="profissao" 
                name="profissao"
                placeholder="Ex.: Desenvolvedora Web"
                [(ngModel)]="contato.profissao"
                required>
            </div>
          </div>

          <!-- Terceira linha -->
          <div class="form-row">
            <div class="form-field">
              <label for="telefone_contato">Telefone para contato</label>
              <input 
                type="tel" 
                id="telefone_contato" 
                name="telefone_contato"
                placeholder="Ex.: 40332019"
                maxlength="8"
                [(ngModel)]="contato.telefone_contato"
                required>
            </div>
            
            <div class="form-field">
              <label for="celular_contato">Celular para contato</label>
              <input 
                type="tel" 
                id="celular_contato" 
                name="celular_contato"
                placeholder="Ex.: 11984932039"
                maxlength="11"
                [(ngModel)]="contato.celular_contato"
                required>
            </div>
          </div>

          <!-- Checkboxes -->
          <div class="checkbox-section">
            <div class="checkbox-item">
              <input 
                type="checkbox" 
                id="whatsapp" 
                name="whatsapp"
                [(ngModel)]="contato.possuiWhatsapp">
              <label for="whatsapp">NÃºmero de celular possui Whatsapp</label>
            </div>

            <div class="checkbox-item">
              <input 
                type="checkbox" 
                id="notificacaoEmail" 
                name="notificacaoEmail"
                [(ngModel)]="contato.notificacaoEmail">
              <label for="notificacaoEmail">Enviar notificaÃ§Ãµes por E-mail</label>
            </div>

            <div class="checkbox-item">
              <input 
                type="checkbox" 
                id="notificacaoSMS" 
                name="notificacaoSMS"
                [(ngModel)]="contato.notificacaoSMS">
              <label for="notificacaoSMS">Enviar notificaÃ§Ãµes por SMS</label>
            </div>
          </div>

          <!-- Mensagem de erro -->
          <div class="error-message" *ngIf="erro">
            {{ erro }}
          </div>

          <!-- BotÃµes de aÃ§Ã£o -->
          <div class="form-actions">
            <button type="submit" class="btn-cadastrar" [disabled]="loading">
              {{ loading ? 'Aguarde...' : (editandoId ? 'Atualizar contato' : 'Cadastrar contato') }}
            </button>
            <button type="button" class="btn-cancelar" *ngIf="editandoId" (click)="cancelarEdicao()">
              Cancelar
            </button>
          </div>
        </form>
      </div>

      <!-- Linha Separadora -->
      <hr class="separator">

      <!-- Filtros de Pesquisa -->
      <div style="margin: 20px 0; padding: 20px; background: #f8f9fa; border-radius: 5px;">
        <h3>ðŸ” Filtros de Pesquisa</h3>
        <div style="display: flex; gap: 15px; flex-wrap: wrap; align-items: center;">
          <div>
            <label for="filtroProfissao" style="display: block; margin-bottom: 5px; font-weight: bold;">ProfissÃ£o:</label>
            <input 
              type="text" 
              id="filtroProfissao"
              [(ngModel)]="filtroProfissao"
              (input)="aplicarFiltros()"
              placeholder="Ex: Desenvolvedora"
              style="padding: 8px; border: 1px solid #ccc; border-radius: 4px; width: 200px;">
          </div>
          <div>
            <label for="filtroAno" style="display: block; margin-bottom: 5px; font-weight: bold;">Ano de Nascimento:</label>
            <input 
              type="text" 
              id="filtroAno"
              [(ngModel)]="filtroAnoNascimento"
              (input)="aplicarFiltros()"
              placeholder="Ex: 1990"
              maxlength="4"
              style="padding: 8px; border: 1px solid #ccc; border-radius: 4px; width: 120px;">
          </div>
          <div style="align-self: end;">
            <button 
              (click)="limparFiltros()"
              style="padding: 8px 15px; background: #6c757d; color: white; border: none; border-radius: 4px; cursor: pointer;">
              ðŸ—‘ï¸ Limpar Filtros
            </button>
          </div>
        </div>
      </div>

      <h2>ðŸ“Š Total: {{ contatosFiltrados.length }} contatos encontrados ({{ contatos.length }} no total)</h2>

      <!-- Mensagem de carregamento -->
      <div *ngIf="loading" style="text-align: center; padding: 20px;">
        <p>Carregando contatos...</p>
      </div>

      <!-- Mensagem quando nÃ£o hÃ¡ contatos -->
      <div *ngIf="!loading && contatos.length === 0" style="text-align: center; padding: 20px;">
        <p>Nenhum contato cadastrado. Cadastre o primeiro contato acima!</p>
      </div>

      <!-- Mensagem quando filtro nÃ£o encontra resultados -->
      <div *ngIf="!loading && contatos.length > 0 && contatosFiltrados.length === 0" style="text-align: center; padding: 20px;">
        <p>ðŸ” Nenhum contato encontrado com os filtros aplicados.</p>
      </div>

      <!-- CONTROLES DE PAGINAÃ‡ÃƒO (TOPO) -->
      <div *ngIf="!loading && contatosFiltrados.length > 0" style="display: flex; justify-content: space-between; align-items: center; margin: 20px 0; padding: 10px; background: #e9ecef; border-radius: 5px;">
        <div>
          <strong>PÃ¡gina {{ paginaAtual }} de {{ totalPaginas }}</strong>
          <span style="margin-left: 10px; color: #6c757d;">
            ({{ contatosPaginados.length }} de {{ contatosFiltrados.length }} contatos)
          </span>
        </div>
        <div style="display: flex; gap: 10px; align-items: center;">
          <button 
            (click)="paginaAnterior()" 
            [disabled]="paginaAtual === 1 || loading"
            style="padding: 8px 12px; background: #007bff; color: white; border: none; border-radius: 4px; cursor: pointer;"
            [style.opacity]="paginaAtual === 1 ? '0.5' : '1'">
            â¬…ï¸ Anterior
          </button>
          <span style="color: #6c757d; font-size: 14px;">{{ paginaAtual }}/{{ totalPaginas }}</span>
          <button 
            (click)="proximaPagina()" 
            [disabled]="paginaAtual === totalPaginas || loading"
            style="padding: 8px 12px; background: #007bff; color: white; border: none; border-radius: 4px; cursor: pointer;"
            [style.opacity]="paginaAtual === totalPaginas ? '0.5' : '1'">
            PrÃ³xima âž¡ï¸
          </button>
        </div>
      </div>

      <!-- TABELA COMPLETA -->
      <div *ngIf="!loading && contatosFiltrados.length > 0">
      <table border="1" style="width: 100%; margin: 20px 0;">
        <tr style="background: #048cd4; color: white;">
          <th style="padding: 10px;">ID</th>
          <th style="padding: 10px;">Nome</th>
          <th style="padding: 10px;">Email</th>
          <th style="padding: 10px;">Data Nascimento</th>
          <th style="padding: 10px;">ProfissÃ£o</th>
          <th style="padding: 10px;">Telefone</th>
          <th style="padding: 10px;">Celular</th>
          <th style="padding: 10px;">AÃ§Ãµes</th>
        </tr>
        <tr *ngFor="let c of contatos" style="background: white;">
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.id }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.nome }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.email }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">{{ formatarData(c.data_nascimento) }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.profissao }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.telefone_contato }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">{{ c.celular_contato }}</td>
          <td style="padding: 10px; border: 1px solid #ccc;">
            <button 
              (click)="editarContato(c)" 
              style="margin-right: 5px; background: #28a745; color: white; border: none; padding: 5px 10px; cursor: pointer; border-radius: 3px;"
              [disabled]="loading">
              Editar
            </button>
            <button 
              (click)="excluirContato(c.id!)" 
              style="background: #dc3545; color: white; border: none; padding: 5px 10px; cursor: pointer; border-radius: 3px;"
              [disabled]="loading">
              Excluir
            </button>
          </td>
        </tr>
      </table>
      </div>
    </div>
  `,
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  contato: Contato = {
    nome: '',
    data_nascimento: '',
    email: '',
    profissao: '',
    telefone_contato: '',
    celular_contato: '',
    possuiWhatsapp: false,
    notificacaoEmail: false,
    notificacaoSMS: false
  };

  contatos: ContatoAPI[] = [];
  contatosFiltrados: ContatoAPI[] = [];
  loading = false;
  erro = '';
  editandoId: number | null = null;

  // PaginaÃ§Ã£o
  paginaAtual = 1;
  itensPorPagina = 6;
  totalPaginas = 0;

  // Filtros
  filtroProfissao = '';
  filtroAnoNascimento = '';

  constructor(private contatoService: ContatoService) { }

  ngOnInit() {
    this.carregarContatos();
  }

  carregarContatos() {
    this.loading = true;
    this.erro = '';
    
    console.log('ðŸ”„ Carregando contatos da API...');
    
    this.contatoService.listarContatos().subscribe({
      next: (response) => {
        console.log('ðŸ“¥ Resposta da API:', response);
        
        if (response && response.data && response.data.contatos) {
          this.contatos = response.data.contatos;
          this.aplicarFiltros();
          console.log('âœ… Contatos carregados:', this.contatos);
        } else {
          console.log('âš ï¸ Estrutura de resposta inesperada:', response);
          this.contatos = [];
          this.contatosFiltrados = [];
        }
        
        this.loading = false;
      },
      error: (error) => {
        console.error('âŒ Erro ao carregar contatos:', error);
        this.loading = false;
        this.erro = 'Erro ao carregar contatos: ' + (error.message || error);
      }
    });
  }

  salvarContato() {
    if (this.loading) return;
    
    if (!this.contato.nome || !this.contato.email) {
      this.erro = 'Nome e email sÃ£o obrigatÃ³rios';
      return;
    }

    this.loading = true;
    this.erro = '';
    
    const operacao = this.editandoId ? 
      this.contatoService.atualizarContato(this.editandoId, this.contato) :
      this.contatoService.criarContato(this.contato);
    
    operacao.subscribe({
      next: (response) => {
        console.log('âœ… Contato salvo:', response);
        this.resetForm();
        this.carregarContatos();
        this.loading = false;
      },
      error: (error) => {
        console.error('âŒ Erro ao salvar contato:', error);
        this.erro = 'Erro ao salvar contato: ' + (error.error?.message || error.message || 'Erro desconhecido');
        this.loading = false;
      }
    });
  }

  excluirContato(id: number) {
    if (confirm('Tem certeza que deseja excluir este contato?')) {
      this.loading = true;
      this.erro = '';
      
      this.contatoService.excluirContato(id).subscribe({
        next: (response) => {
          console.log('âœ… Contato excluÃ­do:', response);
          this.carregarContatos();
          this.loading = false;
        },
        error: (error) => {
          console.error('âŒ Erro ao excluir contato:', error);
          this.erro = 'Erro ao excluir contato: ' + (error.error?.message || error.message || 'Erro desconhecido');
          this.loading = false;
        }
      });
    }
  }

  cancelarEdicao() {
    this.editandoId = null;
    this.resetForm();
  }

  resetForm() {
    this.editandoId = null;
    this.contato = {
      nome: '',
      data_nascimento: '',
      email: '',
      profissao: '',
      telefone_contato: '',
      celular_contato: '',
      possuiWhatsapp: false,
      notificacaoEmail: false,
      notificacaoSMS: false
    };
    this.erro = '';
  }

  formatarData(data: string): string {
    if (!data) return '';
    const date = new Date(data);
    return date.toLocaleDateString('pt-BR');
  }

  trackContatoById(index: number, item: ContatoAPI): any {
    return item.id || index;
  }



  editarContato(contato: ContatoAPI) {
    this.editandoId = contato.id!;
    this.contato = {
      nome: contato.nome,
      data_nascimento: contato.data_nascimento,
      email: contato.email,
      profissao: contato.profissao,
      telefone_contato: contato.telefone_contato,
      celular_contato: contato.celular_contato,
      possuiWhatsapp: false,
      notificacaoEmail: false,
      notificacaoSMS: false
    };
  }

  // MÃ©todos de filtros
  aplicarFiltros() {
    let resultados = [...this.contatos];

    // Filtro por profissÃ£o
    if (this.filtroProfissao.trim()) {
      resultados = resultados.filter(contato => 
        contato.profissao.toLowerCase().includes(this.filtroProfissao.toLowerCase())
      );
    }

    // Filtro por ano de nascimento
    if (this.filtroAnoNascimento.trim()) {
      resultados = resultados.filter(contato => 
        contato.data_nascimento.includes(this.filtroAnoNascimento)
      );
    }

    this.contatosFiltrados = resultados;
    this.calcularPaginacao();
  }

  limparFiltros() {
    this.filtroProfissao = '';
    this.filtroAnoNascimento = '';
    this.aplicarFiltros();
  }

  // MÃ©todos de paginaÃ§Ã£o
  calcularPaginacao() {
    this.totalPaginas = Math.ceil(this.contatosFiltrados.length / this.itensPorPagina);
    if (this.paginaAtual > this.totalPaginas) {
      this.paginaAtual = 1;
    }
  }

  get contatosPaginados(): ContatoAPI[] {
    const inicio = (this.paginaAtual - 1) * this.itensPorPagina;
    const fim = inicio + this.itensPorPagina;
    return this.contatosFiltrados.slice(inicio, fim);
  }

  paginaAnterior() {
    if (this.paginaAtual > 1) {
      this.paginaAtual--;
    }
  }

  proximaPagina() {
    if (this.paginaAtual < this.totalPaginas) {
      this.paginaAtual++;
    }
  }

  irParaPagina(pagina: number) {
    if (pagina >= 1 && pagina <= this.totalPaginas) {
      this.paginaAtual = pagina;
    }
  }

}
