import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface ContatoAPI {
  id?: number;
  nome: string;
  email: string;
  data_nascimento: string;
  profissao: string;
  telefone_contato: string;
  celular_contato: string;
}

export interface APIResponse {
  success: boolean;
  message: string;
  timestamp: string;
  data?: any;
}

@Injectable({
  providedIn: 'root'
})
export class ContatoService {
  private apiUrl = 'http://localhost:8000/api/contatos';

  constructor(private http: HttpClient) {}

  // Listar todos os contatos
  listarContatos(): Observable<APIResponse> {
    return this.http.get<APIResponse>(this.apiUrl);
  }

  // Criar novo contato
  criarContato(contato: ContatoAPI): Observable<APIResponse> {
    return this.http.post<APIResponse>(this.apiUrl, contato);
  }

  // Buscar contato por ID
  buscarContato(id: number): Observable<APIResponse> {
    return this.http.get<APIResponse>(`${this.apiUrl}/${id}`);
  }

  // Atualizar contato
  atualizarContato(id: number, contato: Partial<ContatoAPI>): Observable<APIResponse> {
    return this.http.put<APIResponse>(`${this.apiUrl}/${id}`, contato);
  }

  // Excluir contato
  excluirContato(id: number): Observable<APIResponse> {
    return this.http.delete<APIResponse>(`${this.apiUrl}/${id}`);
  }

  // Buscar por profissão
  buscarPorProfissao(profissao: string): Observable<APIResponse> {
    return this.http.get<APIResponse>(`${this.apiUrl}/profissao/${profissao}`);
  }

  // Buscar por ano de nascimento
  buscarPorAno(ano: number): Observable<APIResponse> {
    return this.http.get<APIResponse>(`${this.apiUrl}/ano/${ano}`);
  }
}
