export class HomeComponent {
  constructor(contatoService, formatacaoService) {
    this.contatoService = contatoService;
    this.formatacaoService = formatacaoService;
    this.contato = {
      nome: '',
      data_nascimento: '',
      email: '',
      profissao: '',
      telefone_contato: '',
      celular_contato: '',
      possuiWhatsapp: false,
      notificacaoEmail: false,
      notificacaoSMS: false
    };
    this.contatos = [];
    this.contatosFiltrados = [];
    this.loading = false;
    this.erro = '';
    this.editandoId = null;
    // Paginação
    this.paginaAtual = 1;
    this.itensPorPagina = 6;
    this.totalPaginas = 0;
    // Filtros
    this.filtroProfissao = '';
    this.filtroAnoNascimento = '';
    // Campos formatados para exibição
    this.telefoneFormatado = '';
    this.celularFormatado = '';
    // Mensagem de sucesso
    this.mensagemSucesso = '';
  }
  ngOnInit() {
    this.carregarContatos();
  }
  carregarContatos() {
    this.loading = true;
    this.erro = '';
    this.contatoService.listarContatos().subscribe({
      next: response => {
        if (response && response.data && response.data.contatos) {
          this.contatos = response.data.contatos;
          this.aplicarFiltros();
        } else {
          this.contatos = [];
          this.contatosFiltrados = [];
        }
        this.loading = false;
      },
      error: error => {
        this.loading = false;
        this.erro = 'Erro ao carregar contatos: ' + (error.message || error);
      }
    });
  }
  salvarContato() {
    if (this.loading) return;
    // Validações básicas
    const validacoes = this.validarFormulario();
    if (validacoes.length > 0) {
      this.erro = validacoes.join(' | ');
      return;
    }
    this.loading = true;
    this.erro = '';
    const operacao = this.editandoId ? this.contatoService.atualizarContato(this.editandoId, this.contato) : this.contatoService.criarContato(this.contato);
    operacao.subscribe({
      next: response => {
        // Limpar erro e exibir mensagem de sucesso
        this.erro = '';
        this.mensagemSucesso = this.editandoId ? 'Contato atualizado com sucesso!' : 'Contato cadastrado com sucesso!';
        // Limpar mensagem após 5 segundos
        setTimeout(() => {
          this.mensagemSucesso = '';
        }, 5000);
        this.resetForm();
        this.carregarContatos();
        this.loading = false;
      },
      error: error => {
        let mensagemErro = 'Erro ao salvar contato: ';
        if (error.status === 422) {
          if (error.error?.errors) {
            // Laravel validation errors
            const erros = Object.values(error.error.errors).flat();
            mensagemErro += erros.join(', ');
          } else {
            mensagemErro += error.error?.message || 'Dados inválidos';
          }
        } else {
          mensagemErro += error.error?.message || error.message || 'Erro desconhecido';
        }
        this.erro = mensagemErro;
        this.loading = false;
