import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { HttpClientModule } from '@angular/common/http';
import { ContatoService, ContatoAPI } from '../../services/contato.service';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, FormsModule, HttpClientModule],
  template: `
    <div class="container">
      <!-- Formulário de Cadastro -->
      <div class="form-container">
        <form class="contact-form" (ngSubmit)="cadastrarContato()">
          <div class="form-row">
            <div class="form-field">
              <label for="nomeCompleto">Nome completo</label>
              <input 
                type="text" 
                id="nomeCompleto" 
                name="nomeCompleto"
                placeholder="Ex.: Letícia Pacheco dos Santos"
                [(ngModel)]="contato.nomeCompleto">
            </div>
            
            <div class="form-field">
              <label for="dataNascimento">Data de nascimento</label>
              <input 
                type="text" 
                id="dataNascimento" 
                name="dataNascimento"
                placeholder="Ex.: 03/10/2003"
                [(ngModel)]="contato.dataNascimento">
            </div>
          </div>

          <div class="form-row">
            <div class="form-field">
              <label for="email">E-mail</label>
              <input 
                type="email" 
                id="email" 
                name="email"
                placeholder="Ex.: leticia@gmail.com"
                [(ngModel)]="contato.email">
            </div>
            
            <div class="form-field">
              <label for="profissao">Profissão</label>
              <input 
                type="text" 
                id="profissao" 
                name="profissao"
                placeholder="Ex.: Desenvolvedora Web"
                [(ngModel)]="contato.profissao">
            </div>
          </div>

          <div class="form-row">
            <div class="form-field">
              <label for="telefone">Telefone para contato</label>
              <input 
                type="tel" 
                id="telefone" 
                name="telefone"
                placeholder="Ex.: (11) 4033-2019"
                [(ngModel)]="contato.telefone">
            </div>
            
            <div class="form-field">
              <label for="celular">Celular para contato</label>
              <input 
                type="tel" 
                id="celular" 
                name="celular"
                placeholder="Ex.: (11) 98493-2039"
                [(ngModel)]="contato.celular">
            </div>
          </div>

          <!-- Checkboxes -->
          <div class="checkbox-section">
            <div class="checkbox-item">
              <input 
                type="checkbox" 
                id="whatsapp" 
                name="whatsapp"
                [(ngModel)]="contato.possuiWhatsapp">
              <label for="whatsapp">Número de celular possui Whatsapp</label>
            </div>
            
            <div class="checkbox-item">
              <input 
                type="checkbox" 
                id="notificacaoEmail" 
                name="notificacaoEmail"
                [(ngModel)]="contato.notificacaoEmail">
              <label for="notificacaoEmail">Enviar notificações por E-mail</label>
            </div>
            
            <div class="checkbox-item">
              <input 
                type="checkbox" 
                id="notificacaoSMS" 
                name="notificacaoSMS"
                [(ngModel)]="contato.notificacaoSMS">
              <label for="notificacaoSMS">Enviar notificações por SMS</label>
            </div>
          </div>

          <!-- Botão de Cadastro -->
          <div class="form-actions">
            <button type="submit" class="btn-cadastrar">Cadastrar contato</button>
          </div>
        </form>
      </div>

      <!-- Linha Separadora -->
      <hr class="separator">

      <!-- Tabela de Contatos -->
      <div class="table-container" *ngIf="contatos.length > 0">
        <table class="contatos-table">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Data de nascimento</th>
              <th>E-mail</th>
              <th>Celular para contato</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let contato of contatos; let i = index">
              <td>{{ contato.nomeCompleto }}</td>
              <td>{{ contato.dataNascimento }}</td>
              <td>{{ contato.email }}</td>
              <td>{{ contato.celular }}</td>
              <td class="actions">
                <button class="btn-edit" (click)="editarContato(i)">
                  <img src="assets/editar.png" alt="Editar" class="action-icon">
                </button>
                <button class="btn-delete" (click)="excluirContato(i)">
                  <img src="assets/excluir.png" alt="Excluir" class="action-icon">
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  `,
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  contato = {
    nome: '',
    data_nascimento: '',
    email: '',
    profissao: '',
    telefone_contato: '',
    celular_contato: ''
  };

  contatos: ContatoAPI[] = [];
  loading = false;
  erro = '';
  editandoId: number | null = null;

  constructor(private contatoService: ContatoService) {}

  ngOnInit() {
    this.carregarContatos();
  }

  carregarContatos() {
    this.loading = true;
    this.contatoService.listarContatos().subscribe({
      next: (response) => {
        if (response.success && Array.isArray(response.data)) {
          this.contatos = response.data;
        } else {
          this.contatos = response.data ? [response.data] : [];
        }
        this.loading = false;
      },
      error: (error) => {
        console.error('Erro ao carregar contatos:', error);
        this.erro = 'Erro ao carregar contatos';
        this.loading = false;
        this.contatos = []; // Manter array vazio em caso de erro
      }
    });
  }

  cadastrarContato() {
    if (!this.contato.nome || !this.contato.email) {
      this.erro = 'Nome e email são obrigatórios';
      return;
    }

    this.loading = true;
    this.erro = '';

    if (this.editandoId) {
      // Atualizar contato existente
      this.contatoService.atualizarContato(this.editandoId, this.contato).subscribe({
        next: (response) => {
          if (response.success) {
            this.carregarContatos();
            this.resetForm();
          }
          this.loading = false;
        },
        error: (error) => {
          console.error('Erro ao atualizar contato:', error);
          this.erro = 'Erro ao atualizar contato';
          this.loading = false;
        }
      });
    } else {
      // Criar novo contato
      this.contatoService.criarContato(this.contato).subscribe({
        next: (response) => {
          if (response.success) {
            this.carregarContatos();
            this.resetForm();
          }
          this.loading = false;
        },
        error: (error) => {
          console.error('Erro ao cadastrar contato:', error);
          this.erro = 'Erro ao cadastrar contato';
          this.loading = false;
        }
      });
    }
  }

  editarContato(contato: ContatoAPI) {
    this.editandoId = contato.id!;
    this.contato = {
      nome: contato.nome,
      data_nascimento: contato.data_nascimento,
      email: contato.email,
      profissao: contato.profissao,
      telefone_contato: contato.telefone_contato,
      celular_contato: contato.celular_contato
    };
  }

  excluirContato(id: number) {
    if (confirm('Tem certeza que deseja excluir este contato?')) {
      this.loading = true;
      this.contatoService.excluirContato(id).subscribe({
        next: (response) => {
          if (response.success) {
            this.carregarContatos();
          }
          this.loading = false;
        },
        error: (error) => {
          console.error('Erro ao excluir contato:', error);
          this.erro = 'Erro ao excluir contato';
          this.loading = false;
        }
      });
    }
  }

  cancelarEdicao() {
    this.editandoId = null;
    this.resetForm();
  }

  resetForm() {
    this.editandoId = null;
    this.contato = {
      nome: '',
      data_nascimento: '',
      email: '',
      profissao: '',
      telefone_contato: '',
      celular_contato: ''
    };
    this.erro = '';
  }

  formatarData(data: string): string {
    if (!data) return '';
    const date = new Date(data);
    return date.toLocaleDateString('pt-BR');
  }
}
